// Component imports
import React from 'react';
import moment from 'moment';
import { Button } from 'react-bootstrap';

export default function Booking({day, lesson, bookings}){
    const targetHour = lesson.start.substring(0, 2);
    const targetMinute = lesson.start.substring(3, 5);
    const targetDate = moment().weekday(1).add(day - 1, 'days');
    targetDate.set('hours', targetHour).set('minutes', targetMinute).set('seconds', 0).set('milliseconds', 0);

    const foundBooking = bookings.find(booking => moment.utc(booking.startTime).isSame(targetDate));

    var timeOver = false;
    if(targetDate.isBefore(moment())) timeOver = true;

    var fremdGebucht = false;

    const onBuchen = () => {
        console.log(`Buchen ${day} ${lesson.key}`);
    };

    const onAusbuchen = () => {
        console.log(`Ausbuchen ${day} ${lesson.key}`);
    };

    if(!foundBooking){
        return(
            <td>
                <Button className="buchenBtn" variant='light' disabled={timeOver} onClick={onBuchen}>
                    Buchen
                </Button>
            </td>
        );
    }else{
        // fremdGebucht = true;
        return(
            <td>
                <Button className="buchenBtn" variant='danger' disabled={timeOver || fremdGebucht} onClick={onAusbuchen}>
                    Gebucht
                </Button>
            </td>
        );
    }
}
