// Component imports
import React, {useState, useEffect} from 'react';
import moment from 'moment';
import { Button } from 'react-bootstrap';

export default function Booking({day, lesson, bookings}){
    const [state, setState] = useState({variant: 'light', disabled: false, onClick: null, text: ""});

    useEffect(() => {
        var targetHour = lesson.start.substring(0, 2);
        var targetMinute = lesson.start.substring(3, 5);
        var targetDate = moment().weekday(1).add(day - 1, 'days');
        targetDate.set('hours', targetHour).set('minutes', targetMinute).set('seconds', 0).set('milliseconds', 0);

        var timeOver = false;
        if(targetDate.isBefore(moment())) timeOver = true;

        var foundBooking = bookings.find(booking => moment.utc(booking.startTime).isSame(targetDate));
        if(!foundBooking){
            setState({...state, variant: 'light', disabled: timeOver, onClick: onBuchen, text: "Buchen"});
        }else{
            var fremdGebucht = true;
            setState({...state, variant: 'danger', disabled: timeOver || fremdGebucht, onClick: onAusbuchen, text: "Gebucht"});
        }
    }, []);

    const onBuchen = () => {
        console.log(`Buchen ${day} ${lesson.key}`);
    };

    const onAusbuchen = () => {
        console.log(`Ausbuchen ${day} ${lesson.key}`);
    };

    if(!foundBooking){
        return(
            <td>
                <Button className="buchenBtn" variant='light' disabled={timeOver} onClick={onBuchen}>
                    Buchen
                </Button>
            </td>
        );
    }else{
        fremdGebucht = true;
        return(
            <td>
                <Button className="buchenBtn" variant='danger' disabled={timeOver || fremdGebucht} onClick={onAusbuchen}>
                    Gebucht
                </Button>
            </td>
        );
    }
}
