// Component imports
import React from 'react';
import {Stack, Spinner} from 'react-bootstrap';

export default function LoadingSpinner({children, loading}){
    if(loading) return <LoadingState />;
    return <>{children}</>;
};

function LoadingState(){
    return(
        <Stack direction='vertical'>
            <Spinner />
        </Stack>
    );
}
