// Component imports
import React, {useState, useEffect, useContext} from 'react';
import { Modal, Button } from 'react-bootstrap';
import {HomePageContext} from '../../contexts/HomePageContext';

export default function BookingModal(){
    const {hpContext, setHpContext} = useContext(HomePageContext);
    const [state, setState] = useState(null);

    useEffect(() => {
        if(hpContext.selectedBooking !== null){
            setState({day: hpContext.selectedBooking.day, lesson: hpContext.selectedBooking.lesson});
        }else{
            setState(null);
        }
    }, [hpContext.selectedBooking]);

    useEffect(() => {

    }, [state]);

    const onCancel = () => {
        setHpContext({...hpContext, selectedBooking: null});
    };

    const onSubmit = () => {
        setHpContext({...hpContext, selectedBooking: null});        
    };

    if(state === null) return null;
    return(
        <Modal show={state !== null} onHide={onCancel}>
            <Modal.Header>
                <Modal.Title>Buchung</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <p>Stunden: {state.duration}</p>
            </Modal.Body>
            <Modal.Footer>
                <Button variant='ghost' onClick={onCancel}>Abbrechen</Button>
                <Button variant='primary' onClick={onSubmit}>Speichern</Button>
            </Modal.Footer>
        </Modal>
    );
};
