// Component imports
import React, {useState, useEffect, useContext} from 'react';
import { HomePageContext } from '../../contexts/HomePageContext';
import StundenplanBooking from './StundenplanBooking';
// API imports
import BookingsAPI from '../../api/bookings';
import timesMap from '../../api/timesMap.json';

export default function StundenplanBody(){
    const {hpContext, setHpContext} = useContext(HomePageContext);
    const [bookings, setBookings] = useState([]);
    const [elements, setElements] = useState([]);

    useEffect(() => {
        setBookings(BookingsAPI.getBookings());
    }, [hpContext]);

    useEffect(() => {
        var newElements = [];
        timesMap.forEach(lesson => {
            newElements.push(<StundenplanRow key={`row_${lesson.key}`} lesson={lesson} bookings={bookings} weekOffset={hpContext.weekOffset} />);
        });
        setElements(newElements);
    }, [bookings]);

    return(
        <tbody>
            {elements}
        </tbody>
    );
}

function StundenplanRow({lesson, bookings, weekOffset}){
    const [elements, setElements] = useState([]);

    useEffect(() => {
        var newElements = [];
        for(var day = 1; day <= 5; day++){
            if(lesson.key !== "break"){
                newElements.push(<StundenplanBooking key={`booking_${day}_${lesson.key}`} bookings={bookings} day={day} lesson={lesson} />);
            }
        }
        setElements(newElements);
    }, []);

    return(
        <tr>
            <StundenplanRowTitle lesson={lesson} />
            {elements}
        </tr>
    );
}

function StundenplanRowTitle({lesson}){
    const [title, setTitle] = useState("");

    useEffect(() => {
        if(lesson.key !== "break"){
            setTitle(`Stunde ${lesson.key}`);
        }else{
            setTitle("Pause");
        }
    }, []);

    return(
        <td key="title">
            <div className="rowTitle">
                <span><b>{title}</b></span>
                <span>{lesson.start} - {lesson.end}</span>
            </div>
        </td>
    );
}
