const apiRequest = require('./base').apiRequest;

export default async function apiGetBookings(roomId, startDate, endDate){
  var response = await apiRequest(`rooms/${roomId}/bookings`, "POST", {
    startDate: startDate,
    endDate: endDate
  });
  return response.data;
}

export async function book(roomId, startTime, endTime, note){
  var returnValue;

  await apiRequest("bookings", "PUT", {
    roomID: roomId,
    startTime: startTime,
    endTime: endTime,
    note: note,
    username: localStorage.getItem("loginUsername")
  })
  .then(response => {
    returnValue = response.data;
  });

  return returnValue;
}

