// Component imports
import React, {createElement, useEffect, useState} from 'react';
import {Stack, Navbar, Button, NavDropdown, Table, Nav} from 'react-bootstrap';
import timesMap from '../api/timesMap.json';
// API imports
import BookingsAPI from '../api/bookings';

export default function HomePage(){
    return(
        <Stack direction='vertical'>
            <AppNavBar />
            <Stundenplan />
        </Stack>
    );
}

function AppNavBar(){
    const [rooms, setRooms] = useState([]);
    const [classes, setClasses] = useState([]);

    const debugRooms = [
        "304",
        "307",
        "Tobias sei Mama"
    ];

    const debugClasses = [
        "1ISB1",
        "3ISBA1"
    ];

    useEffect(() => {
        generateRooms();
        generateClasses();
    }, []);

    function generateRooms(){
        setRooms(debugRooms.map(room => <NavDropdown.Item key={`room_${room}`}>{room}</NavDropdown.Item>));
    }

    function generateClasses(){
        setClasses(debugClasses.map(target => <NavDropdown.Item key={`class_${target}`}>{target}</NavDropdown.Item>));
    }

    return(
        <Navbar bg='light' expand='lg'>
            <Stack direction='horizontal' style={{width: '100%', justifyContent: 'space-between'}}>
                <Navbar.Brand>CHECK-IT</Navbar.Brand>
                <Stack direction='horizontal' gap={3}>
                    <NavDropdown title='Raum'>
                        {rooms}
                    </NavDropdown>
                    <NavDropdown title='Klasse'>
                        {classes}
                    </NavDropdown>
                    <Button>Abmelden</Button>
                </Stack>
            </Stack>
        </Navbar>
    );
}

function Stundenplan(){
    const [tableHead, setTableHead] = useState([]);
    const [tableBody, setTableBody] = useState([]);
    const [mondayDate, setMondayDate] = useState(undefined);

    const debugBookings = [
        {
          "id": 0,
          "startTime": "2022-09-28T11:39:19.065Z",
          "endTime": "2022-09-28T11:39:19.065Z",
          "room": 0,
          "userId": 0,
          "createTime": "2022-09-28T11:39:19.065Z",
          "createdBy": 0
        }
      ];

    useEffect(() => {
        generateTableHead();
        generateTableBody();
    }, []);

    function generateTableHead(){
        var weekOffset = 0;
        var dateToday = new Date();
        dateToday.setDate(dateToday.getDate() + (7 * weekOffset));

        var weekDayToday = dateToday.getDay();
        var dateMonday = new Date(dateToday);
        dateMonday.setDate(dateToday.getDate() - (weekDayToday - 1));
        setMondayDate(dateMonday);

        var newTableHead = [];
        newTableHead.push(<th key="th0"></th>);
        for(var i = 0; i < 5; i++){            
            var useDate = new Date(dateMonday);
            useDate.setDate(useDate.getDate() + i);
            newTableHead.push(<th key={`th${i + 1}`}>{useDate.getDate()}.{useDate.getMonth() + 1}.{useDate.getFullYear()}</th>);
        }
        setTableHead(newTableHead);
    }

    function generateTableBody(){
        var newTableBody = [];

        timesMap.forEach(lesson => {
            newTableBody.push(generateRow(lesson));
        });

        setTableBody(newTableBody);
    }

    function generateRow(lesson){
        var tempData = [];

        if(lesson.key === "break"){
            tempData.push(<td>Pause</td>);
        }else{
            tempData.push(<td>Stunde {lesson.key}</td>);
        }

        for(var day = 1; day <= 5; day++){
            tempData.push(generateBooking(day, lesson));
        }

        var tempRow = <tr key={lesson.key}>{tempData}</tr>;
        if(lesson.key === "break") return tempRow;

        return tempRow;
    }

    function generateBooking(day, lesson){
        var targetDate = new Date(mondayDate);
        console.log(targetDate);
        targetDate.setDate(targetDate.date + day - 1);
        console.log(targetDate);

        var fittingBooking;
    }

    return(
        <Table>
            <thead>
                <tr>
                    {tableHead}
                </tr>
            </thead>
            <tbody>
                {tableBody}
            </tbody>
        </Table>
    );
}
