// Component imports
import React, {useEffect, useState, useContext} from 'react';
import {Stack, Button, Table, Modal, Form} from 'react-bootstrap';
import AppNavBar from './components/AppNavBar';
import AppNavRooms from './components/AppNavRooms';
import RoomsContextProvider, {RoomsContext} from '../contexts/RoomsContext';
import RoomModal from './components/RoomModal';
// Style imports
import '../css/components/RoomsPage.css';
// API imports
const RoomsAPI = require('../api/rooms');

export default function RoomsPage(){
    return(
        <RoomsContextProvider>
            <Stack direction='vertical'>
                <AppNavBar>
                    <AppNavRooms />
                </AppNavBar>
                <RoomBody />
                <RoomModal />
            </Stack>
        </RoomsContextProvider>
    );
}

function RoomBody(){
    return(
        <Table className='rooms'>
            <thead>
                <tr>                    
                    <th>Raumnummer</th>
                    <th>Raumname</th>                        
                    <th></th>
                </tr>
            </thead>
            <tbody>                    
                <RoomRow />
            </tbody>
        </Table>
    );
}

function RoomRow(){
    const [rooms, setRooms] = useState(null);
    const [elements, setElements] = useState([]);
    const {roomsContext, setRoomsContext} = useContext(RoomsContext);

    const onEdit = () => {
        setRoomsContext({...roomsContext, uiControl:{...roomsContext.uiControl, roomModal: true, modalMode: "edit"}});
    };

    const onDelete = () => {
        setRoomsContext({...roomsContext, uiControl:{...roomsContext.uiControl, roomModal: true, modalMode: "delete"}});
    };

    useEffect(() => {
        async function doAsync(){
            var tempRooms = await RoomsAPI.getRooms();
            await setRooms(tempRooms.data);
        };
        doAsync();
    }, []);

    useEffect(() => {
        setElements(rooms?.map(room => room.active ? 
            <tr key={`room_${room.id}`}>
                <td key={`room_${room.number}`}>{room.number}</td>
                <td key={`room_${room.name}`}>{room.name}</td>
                <td>
                    <Button onClick={onEdit} className="my-1 me-2">
                        Raum bearbeiten
                    </Button>
                    <Button variant="danger" className="my-1 me-2">
                        Raum l√∂schen
                    </Button>
                </td> 
            </tr>
            : null));
    }, [rooms]);


    return(
        <>
            {elements}
        </>
    );
}
