// Component imports
import React, {useState, useEffect, useContext} from 'react';
import {Navbar, Stack, Nav, NavDropdown, Button} from 'react-bootstrap';
import {useNavigate} from 'react-router-dom';
import {HomePageContext} from '../../contexts/HomePageContext';
// API imports
import RoomsAPI from '../../api/rooms';

export default function AppNavBar(){
    const {hpContext, setHpContext} = useContext(HomePageContext);
    const navigate = useNavigate();
    const [rooms, setRooms] = useState([]);
    const [roomElements, setRoomElements] = useState([]);

    useEffect(() => {
        getRooms();
    }, []);

    useEffect(() => {
        generateRooms();
    }, [rooms]);

    function getRooms(){
        setRooms(RoomsAPI.getRooms());
    }

    function generateRooms(){
        setRoomElements(rooms.map(room => <NavDropdown.Item key={`room_${room.number}`}>{room.name}</NavDropdown.Item>));
    }

    const onLogout = () => {
        navigate("/login");
    };

    const onRoomSelect = (eins, zwei) => {
        console.log(eins);
        console.log(zwei);
        // console.log(name)
        // setHpContext({...hpContext, roomName: name});
    }

    return(
        <Navbar bg='light' expand='lg'>
            <Stack direction='horizontal' style={{width: '100%', justifyContent: 'space-between'}}>
                <Navbar.Brand>CHECK-IT</Navbar.Brand>
                <Nav>
                    <NavDropdown title={hpContext.roomName ? hpContext.roomName : "Raum"} align={'end'} onSelect={onRoomSelect}>
                        {roomElements}
                    </NavDropdown>
                    <Nav.Item>Test</Nav.Item>
                    <Button onClick={onLogout}>Abmelden</Button>
                </Nav>
            </Stack>
        </Navbar>
    );
};
