// Component imports
import React, {useEffect, useState} from 'react';
import {Stack, Navbar, Button, NavDropdown, Table} from 'react-bootstrap';
// API imports
import BookingsAPI from '../api/bookings';

export default function HomePage(){
    return(
        <Stack direction='vertical'>
            <AppNavBar />
            <Stundenplan />
        </Stack>
    );
}

function AppNavBar(){
    return(
        <Navbar bg='light' expand='lg'>
            <Stack direction='horizontal' style={{width: '100%', justifyContent: 'space-between'}}>
                <Navbar.Brand>CHECK-IT</Navbar.Brand>
                <Stack direction='horizontal' gap={3}>
                    <NavDropdown title='Klasse'>
                        <NavDropdown.Item>1ISBA1</NavDropdown.Item>
                        <NavDropdown.Item>3ISBA1</NavDropdown.Item>
                    </NavDropdown>
                    <NavDropdown title='Raum'>
                        <NavDropdown.Item>304</NavDropdown.Item>
                        <NavDropdown.Item>307</NavDropdown.Item>
                        <NavDropdown.Item>Tobias sei Mama</NavDropdown.Item>
                    </NavDropdown>
                    <Button>Abmelden</Button>
                </Stack>
            </Stack>
        </Navbar>
    );
}

function Stundenplan(){
    const [tableHead, setTableHead] = useState([]);
    const [tableBody, setTableBody] = useState([]);

    useEffect(() => {
        var weekOffset = 2;
        var dateToday = new Date();
        dateToday.setDate(dateToday.getDate() + (7 * weekOffset));

        var weekDayToday = dateToday.getDay();
        var dateMonday = dateToday;
        dateMonday.setDate(dateToday.getDate() - (weekDayToday - 1));

        var newTableHead = [];
        newTableHead.push(<th></th>);
        for(var i = 0; i < 5; i++){            
            var useDate = dateMonday;
            useDate.setDate(useDate.getDate() + (i - 1));
        }

    }, []);

    return(
        <Table>
            <thead>
                <tr>
                    {tableHead}
                </tr>
            </thead>
            <tbody>
                {tableBody}
            </tbody>
        </Table>
    );
}
