const axios = require('axios').default;

const SERVER_CONF = {
    ADDRESS: "10.0.5.57",
    PORT: "7061"
};

async function apiRequest(path, method, data){
    var dings = null;
    await axios({
        url: `https://${SERVER_CONF.ADDRESS}:${SERVER_CONF.PORT}/${path}`,
        method: method,
        data: data
    })
    .then((response) => {
        dings = response.data;
    });
}

module.exports = {
    apiRequest
};
