// Component imports
import React, {useState, useEffect, useContext} from 'react';
import {Navbar, Stack, NavDropdown, Button} from 'react-bootstrap';
import {useNavigate} from 'react-router-dom';
// Context imports
import { AppContext } from '../../AppContext';
// API imports
import RoomsAPI from '../../api/rooms';

export default function AppNavBar(){
    const {contextData, setContextData} = useContext(AppContext);
    const navigate = useNavigate();
    const [rooms, setRooms] = useState([]);
    const [roomElements, setRoomElements] = useState([]);
    const [selectedRoom, setSelectedRoom] = useState("Raum");

    useEffect(() => {
        getRooms();
    }, []);

    useEffect(() => {
        generateRooms();
    }, [rooms]);

    function getRooms(){
        setRooms(RoomsAPI.getRooms());
    }

    function generateRooms(){
        setRoomElements(rooms.map(room => <NavDropdown.Item key={`room_${room}`}>{room}</NavDropdown.Item>));
    }

    const onRoomSelected = (id) => {
        setContextData({...contextData, roomId: id});
    };

    const onLogout = () => {
        navigate("/login");
    };

    return(
        <Navbar bg='light' expand='lg'>
            <Stack direction='horizontal' style={{width: '100%', justifyContent: 'space-between'}}>
                <Navbar.Brand>CHECK-IT</Navbar.Brand>
                <Stack direction='horizontal' gap={3}>
                    <NavDropdown title={selectedRoom} align={'end'}>
                        {roomElements}
                    </NavDropdown>
                    <Button onClick={onLogout}>Abmelden</Button>
                </Stack>
            </Stack>
        </Navbar>
    );
};
