// Component imports
import React, {useState, useEffect} from 'react';
import moment from 'moment';
import { Button, ButtonGroup } from 'react-bootstrap';

export default function StundenplanHead(){
    const [weekOffset, setWeekOffset] = useState(0);
    const [elements, setElements] = useState([]);

    useEffect(() => {
        var dateMonday = moment().weekday(1).add(weekOffset, 'weeks');

        var newElements = [];
        newElements.push(<th key="th_0">
            <ButtonGroup>
                <Button disabled={weekOffset <= 0 ? true : false} onClick={onEarlier}>Früher</Button>
                <Button disabled={weekOffset >= 3 ? true : false} onClick={onLater}>Früher</Button>
            </ButtonGroup>
        </th>);
    }, []);

    const onEarlier = () => {
        setWeekOffset(weekOffset - 1);
    }

    const onLater = () => {
        setWeekOffset(weekOffset + 1);
    }

    return(
        <thead>
            <tr>
                {elements}
            </tr>
        </thead>
    );
}
