// Component imports
import React, {useContext, useEffect, useState} from 'react';
import {Button, Modal, Form} from 'react-bootstrap';
import { RoomsContext } from '../../contexts/RoomsContext';
// API imports
const RoomsAPI = require('../../api/rooms');

export default function RoomModal(){
    const {roomsContext, setRoomsContext} = useContext(RoomsContext);
    const [state, setState] = useState(null);

    useEffect(() => {
        if(roomsContext.rooms.selected !== null){
            setState(roomsContext.rooms.selected);
        }else{
            setState(null);
        }
    })

    return(
        <Modal show={true} centered>
            <Modal.Header closeButton>
                <Modal.Title>Raum</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group className="mb-3" controlId="setNumber">
                        <Form.Label>Nummer</Form.Label>
                        <Form.Control type="text"/>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="setName">
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text"/>
                    </Form.Group> 
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant='ghost'>Abbrechen</Button>
                <Button variant='danger'>LÃ¶schen</Button>
                <Button variant='primary'>Erstellen / Speichern</Button>
            </Modal.Footer>
        </Modal>
    );
};
