const apiRequest = require('./base').apiRequest;

async function getRooms(){
    return await apiRequest('rooms', "GET", null);
}

async function addRoom(number, name){
    return await apiRequest('rooms', "PUT", {
        number: number,
        name: name,
        active: true
    })
}

module.exports = {
    getRooms
};
